void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.1415926535;
    float _Value = abs(sin(iTime/4.));

    vec2 xy = fragCoord.xy / iResolution.xy;
    float r = min(_Value, xy.y);

    vec4 col = vec4(0., 0., 0., 1.);

    col.r = 8. * ( clamp(-cos(2. * PI * r * .45), 0., 1.) + clamp(0.4*sin(2. * PI * r * 0.5 + PI / 1.17), 0., 1.));
    col.g = 1.5 * clamp(sin(2. * PI * r * .88 - PI/2.), 0., 1.);
    col.b = 8. * clamp(cos(2. * PI * r * .59), 0., 1.);


    fragColor = col;
}
