// Fork of "Depth_04" by OneMoth. https://shadertoy.com/view/4fj3Ry
// 2024-01-05 14:21:31

vec3 palette (float t){

vec3 a = vec3(0.746, 0.114, 0.325);
vec3 b = vec3(0.362, 0.229, 0.476);
vec3 c = vec3(0.735, 1.290, 0.433);
vec3 d = vec3(1.431, 1.870, 2.782);

return a + b * cos(6.28318*(c*t+d));
}

vec2[] aa = vec2[](vec2(.31, .18),vec2(-.18,.31),vec2(-.31,-.18),vec2(.18,-.31));

void mainImage (out vec4 fragColor, in vec2 fragCoord){
  float pix = 1. / iResolution.y;
  vec3 aacolor = vec3(0.0);
  vec3 abcolor = vec3(0.0);
  for(int k = 0; k < 4; k++) {
  for(int j = 0; j < 4; j++) {

  vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
  uv += (aa[j] * pix / 2.) + (aa[k] * pix);
  vec2 uv0 = uv;
  vec3 finalColor = vec3(0.0);

  for (float i = 0.0; i < 2.0; i++){
      uv = uv * 5.0 - 2.0;
      float d = length(uv) * exp(-length(uv0));
      vec3 col = palette(length(uv0) + i * 0.5 + iTime * 1.0);
      d = sin(d*3. - iTime) / 10.0;
      d = abs(d);
      d = pow(0.01 / d, 1.0);
      finalColor += col * d;
  }
  aacolor += 0.25*finalColor;
  }
  abcolor += 0.125*aacolor;
  }
  fragColor = vec4(abcolor, 1.0);
}
