void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = 0.5;

    //move in circles
    fragCoord.x += cos(iTime)*180.0;
    fragCoord.y += sin(iTime)*60.0;

    //spin
    float x = fragCoord.x - iResolution.x*0.5;
    float y = fragCoord.y - iResolution.y*0.5;
    float angle = -1.0*iTime*1.0 + 1.57;
    fragCoord.x = x*cos(angle) - y*sin(angle) + iResolution.x*0.5;
    fragCoord.y = x*sin(angle) + y*cos(angle) + iResolution.y*0.5;


    vec2 rv = fragCoord - vec2(iResolution.x*0.5, iResolution.y*0.5);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 rnv = rv/max(iResolution.x, iResolution.y);
    float r = length(rnv);

    float polar = atan(rnv.y, rnv.x);
    bool isHead = polar > 1.08 && polar < 2.06;
    //float fin = isHead ? 0.0 : 50.0*(0.5*sin(iTime*3.0)+0.5)*rnv.x;
    float finT = sin(2.0*iTime);
    float fin = 100.0*(0.5*finT+0.5)*rnv.x;

    float tentacleL = size*0.02*cos(
        8.0*atan(rnv.y, rnv.x) 
        + fin 
        + cos(iTime*2.0)*1.0 // flip fins left right a bit
        );


    // Time varying pixel color
    vec3 col1 = 0.5 + 0.5*cos(iTime+rnv.xyx+vec3(0,2,4));
    vec3 backCol = vec3(0.0, 0.0, 0.0);
    //vec3 col1 = vec3(1.0, 1.0, 0.0);
    vec3 col = r > size*0.1 + tentacleL ? backCol : col1;

    // Output to screen
    fragColor = vec4(col,1.0);
}
