void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 image = texture(iChannel0, uv);
    float avg = (image.r + image.g + image.b) / 3.;
    float offset = iMouse.y > 0. ? iMouse.x/iResolution.x - .5 : 0.;

    vec4 threshold = vec4(
      vec3(round(
        clamp(0., avg + offset, 1.))),
    1.);

    fragColor = threshold;
}
