float cell(vec2 p){
    return max(
               length(max(abs(p) - vec2(.11), 0.)),
               -(length(p) - .11)
           );
}

float grid(vec2 p, float a, float b, float t) {

    float idx, idy,
          w = 3.14 / 12.,
          l = length(p),
          h = 1.;

    p = vec2(
            atan(p.x, p.y),
            log(l)
        );

    idx  = floor(p.x / w);
    idy  = floor(p.y / w);


    if(idx == a)
        p.y += t * w;

    if(idy == b)
        p.x += t * w;

    p = mod(p, w) - w / 2.;

    return l * cell(p);
}



void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy; 
         u = vec2(u + u - r) / r.y;

    float d, t = mod(iTime, 3.14 / 12.);

    d = grid(u, 2., -1., t * t);
    if(sin(iTime * 4.) > .5)
        d = grid(u, 2., 2., t * t);


    o = vec4(smoothstep(.0, 3. / r.y, d)) * .3 + .1;
}
