// Cartoon Roof Texture by Rik Riesmeijer, 2024 - No rights reserved.
void mainImage(out vec4 c, vec2 v) {
    c = vec4(0,0.05,0.0,1);
    v.x *= 1.15;
    v /= 100.0;
    v.x += fract(0.5 - 0.5 * ceil(v.y + 0.5));
    v = abs(0.5 - fract(v));
    float factor = v.x * v.y * fract(0.5 * ceil(v.y));
    c.r = 0.5 * smoothstep(-0.001, 0.005, factor);
    c.r += 0.1 * smoothstep(0.4, 1.0, fract(v.x * 2.0));
}
