vec3 palette(float t) {
    vec3 a = vec3(1.000,1.000,1.000);
    vec3 b = vec3(1.000,0.000,0.584);
    vec3 c = vec3(1.000,0.000,0.682);
    vec3 d = vec3(0.000,0.431,1.000);

    return a + b*cos(6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; 
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i <7.0; i++){

        uv = fract(uv * .5) -0.5;
        float d = length(uv);
        vec3 col = palette(iTime/99.0 + length(uv0));
        d = abs(d);
        d = .02 / d;
        finalColor += col * d;

    }

    // Add vignette effect
    vec2 vignetteUV = fragCoord / iResolution.xy;
    vignetteUV = (vignetteUV - 0.5) * 2.0;
    float vignette = 1.0 - dot(vignetteUV, vignetteUV);
    vignette = pow(vignette, 1.5);

    finalColor *= vignette;

    fragColor = vec4(finalColor, 1.0);
}
