float dot2(in vec2 v ) { return dot(v,v); }
float sdTunnel( in vec2 p, in vec2 wh )
{
    p.x = abs(p.x); p.y = -p.y;
    vec2 q = p - wh;

    float d1 = dot2(vec2(max(q.x,0.0),q.y));
    q.x = (p.y>0.0) ? q.x : length(p)-wh.x;
    float d2 = dot2(vec2(q.x,max(q.y,0.0)));
    float d = sqrt( min(d1,d2) );

    return (max(q.x,q.y)<0.0) ? -d : d;
}


vec3 palette ( float t, vec3 a, vec3 b, vec3 c, vec3 d){
    return a + b * cos(6.28318 * (c*t+d));
}
vec3 myColorfulPalette (float t){
    vec3 a = vec3(0.059,0.325,0.859);
    vec3 b = vec3(0.631,0.329,0.227);
    vec3 c = vec3(0.059,0.722,0.612);
    vec3 d = vec3(0.522,0.114,0.604);
    return palette(t, a,b,c,d);
}

float complexity = 0.1;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =(fragCoord *2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.1);
    complexity =  complexity* sin(iTime * 0.123 ) *15.1 ;

    for( float i= 0.0; i< 4.0; i=i+0.9){
        uv = fract(uv*complexity) - 0.5;


        float d = length (uv) * exp(-length(uv0));

        vec3 col = myColorfulPalette(length(uv0)+ i*.4 + iTime *2.0);

        d = sin(d * 8. + iTime) /8.;
        d = abs(d);
        d = pow( 0.01 / d, 1.2);



        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);

}
