void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = kawaseBlur(iChannel0, fragCoord, iResolution.xy, 3.5); 
}

vec4 kawaseBlur(sampler2D tex, vec2 fragCoord, vec2 res, float d)
{
    vec2 s0 = (fragCoord + vec2(d, d)) / res;
    vec2 s1 = (fragCoord + vec2(d, -d)) / res;
    vec2 s2 = (fragCoord + vec2(-d, d)) / res;
    vec2 s3 = (fragCoord + vec2(-d, -d)) / res;

    float lod = 1.5;
    return (textureLod(tex,s0,lod)+textureLod(tex,s1,lod)+textureLod(tex,s2,lod)+textureLod(tex,s3,lod)) / 4.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = kawaseBlur(iChannel0, fragCoord, iResolution.xy, 0.5); 
}

