void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rot = 0.1 * iTime;
    vec2 uv = fragCoord/iResolution.xy * 2.0 -1.0;

    for (float i = 0.0; i < 20.0;i++)
    {
        //uv.x = abs(uv.x);
        uv -= 0.5;
        uv *= 1.1;

        uv *= mat2(-cos(rot), sin(rot),
                    -sin(rot), -cos(rot));
        //uv.x = fract(uv.x)*0.6;
        uv.x += cos(uv.y);
        //uv.y = fract(uv.y);
        uv.y -= cos(uv.x);
        //uv = vec2(fract(uv.x - uv.y), uv.y);

    }
    uv = normalize(uv);
    uv = vec2(1.0,1.0)-uv;

    fragColor = vec4(
        1.0 - length(uv + vec2(-0.4,-0.4)),
        1.0 - length(uv + vec2(-0.3,-0.35)),
        1.0 - length(uv + vec2(-0.35,-0.4)),1.0);}
