/*
    As always, use however you like, take it apart, turn it into a square, print it out and use it as firestarter.
    But if you're going to sell it, maybe think about crediting me? Thanks.
*/

vec3 colors[10] = vec3[10] ( //10 colors for my Quantization function https://www.shadertoy.com/view/XcKGWD
    vec3(0.),
    vec3(1.),
    vec3(1., 0., 0.),
    vec3(.5, 0., 0.),
    vec3(.4, .12, .12),
    vec3(0., 1., 1.),
    vec3(0., .7, .7),
    vec3(0., .25, .25),
    vec3(.5),
    vec3(.25)
);

vec3 Quantize(vec3 col) { //Quantization function see my older shader for more information
    float dist = distance(colors[0], col); 
    vec3 tempColor = colors[0]; 

    for (int i = 1; i < colors.length(); i++) { 
        if (distance(colors[i], col) < dist) { 
            dist = distance(colors[i], col);
            tempColor = colors[i]; 
        }
    }

    return tempColor;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y); //normalize uv

    vec3 col; //define a vec3 for our color
    col = vec3(1.);

    if (sin(iTime / 3.) >= 0.) {
        col += (texture(iChannel0, uv).xxx / 2.); //add in the noise (for dithering)
    }

    col *= uv.xyy;

    if (sin(iTime / 2.) >= 0.) { //same idea as the cos()
        col = Quantize(col); //quantize it so it uses as few colors as possible
    }

    fragColor = vec4(col, 1.);
}
