void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 6.0 * fragCoord/iResolution.xy;
    for(int i = 1; i < 10; i++ ){
        float n = float(i);
        uv += vec2(2.5 / n * sin(n * uv.y + iTime + 1.3 * n) + 1.8, 1.4 /  n * sin(uv.x + iTime + 2.3 * n) + 5.8);
     }
    vec3 color = vec3(0.0, 0.0, sin(uv.x + uv.y));
    fragColor = vec4(color,1.0);
}
