#define M iMouse
#define S .01 // Scroll speed
#define F vec2(.3,.5) // Offset
#define L .1 // Speed offset when dragging
#define H iChannel0

void mainImage(out vec4 c, in vec2 u)
{
    u /= iResolution.xy;
    float W = (M.z >= 1.) ? M.y * L : iTime;
    vec4 a = vec4(u + W * S, F + u - W * S);
    c = texture(H, a.xy) * texture(H, a.zw);
}
