#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(3,2,1.5),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*6.;
    p.xz*=R2D(t*.3);
    p.xy=mod(p.xy,1.5);

    for(j=0.;j++<8.;)
        p=abs(abs(p)*e-q)-1.,
        d=min(max(p.x,p.y), p.z),
        d-=dot(p,q)/2e4;

    e+=d;
}o+=vec4(log(-p*d),.1)*3./i;
}
