#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

const float eps=0.0001;

float sdBox(vec3 p,vec3 s)
{
    vec3 q=abs(p)-s;
    return length(max(q,0.))+min(max(max(q.y,q.z),q.x),0.);
}

float map(vec3 p)
{
    p.x += sin(iTime)*.38;
    p.y -= sin(iTime)*.02;
    p.xy*=rot(iTime);
    p = mod(p, 0.8) - 0.4;

    return sdBox(p,vec3(.2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    float dist, hit, i, mainEmissive = 0.;
    vec3 cPos = vec3(0., 0., 5.-iTime*2.);
    vec3 cDir = normalize(vec3(0., 0., -1.));
    vec3 cUp = vec3(0., 1., 0.);
    vec3 cSide = cross(cDir, cUp);
    vec3 ray = normalize(cSide*uv.x+cUp*uv.y+cDir); 
    vec3 L = normalize(vec3(1));
    vec3 col=vec3(0);

    for(;i<64.;i++)
    {
        vec3 rp=cPos+ray*hit;
        dist=map(rp);
        hit+=dist;
        mainEmissive+=exp(abs(dist)*-.2);
        if(dist<eps)
        {
            break;
        }
        col = 0.03*mainEmissive*vec3(1.0, 0.5, 0.1);
    }

    fragColor = vec4(col,1.);
}
