#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

float fsnoise (vec2 c){
    return fract(sin(dot(c, vec2(12.9898, 78.233))) * 43758.5453);}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-2,0,2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1);
    p.yz*=fsnoise(p.xy)-R2D(t*.5);

    for(j=0.;j++<3.;)
        p=abs((p*e)-q),
        d=max(min(p.z,p.y)/d,p.x/d),
        d-=dot(p,q)/2e4;

    e+=d;

}o+=vec4(exp(p*q/d/e),1)*5./i;
}
