#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main {

    U = 2.*(U-.5*R)/R.y;
    Q = vec4(0);
    for (float i = 0.; i < 102.; i++) {
        U = vec2(U.x*U.x-U.y*U.y,2.*U.x*U.y);
        U.x = abs(U.x);
        U.x -= .8;
        U.xy *= ei(0.05);
        if (length(U)>10.) break;
            Q += .15*exp(-10.*length(U));

    }


}

Main {
    Q = B(U);
    vec4 h = hash(vec4(U,iFrame,iTime));

    float a = 3.1415926*h.x*2.;

    vec2 d = vec2(cos(a),sin(a));
    vec2 p = U;
    float s = 0.;
    for (float i = 0.; i < 200.; i++) {
        vec4 a = A(p);
        Q += .1*(
            step(length(p-vec2(.5,.5)*R),.05*R.y)*
            max(sin(-.5+s+vec4(1,2,3,4)),0.)+
            step(length(p-vec2(.3,.5)*R),.05*R.y)*
            max(sin(-1.5+s+vec4(1,2,3,4)),0.)+
            step(length(p-vec2(.7,.5)*R),.05*R.y)*
            max(sin(.5+s+vec4(1,2,3,4)),0.));

        p += 2.*d;
        d += a.xy;
        s += .1*length(a.w*a.xy);
        d = normalize(d);
    }

}
