{
    vec2 r = 2.0 * vec2(fragCoord.xy - 0.5 * iResolution.xy)/iResolution.x;

    vec3 col = vec3(1.0, 1.0, 1.0);

    float scale = 0.5 - r.y * r.y;

    float x = 0.5 + 0.5 * cos(r.x * scale * sqrt(900. * iTime));

    vec3 extra = vec3(1.0,0.,0.);

    vec3 pumpkin = vec3(255., 117., 24.) / 255.;

    vec3 color = pumpkin + x * extra;

