#define R3(a) mix(q*dot(p,q),p,cos(a))+sin(a)*cross(p,q)
#define R2(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,h; vec3 p,q=vec3(-1,.2,2.7);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=.4;
    p=R3(iTime*.6);
    p.xz*=R2(iTime);

    for(j=0.;j++<4.;)
        p=abs(abs(p)-.005)-.001,
        h=clamp(dot(p,q)/dot(p,p),0.,1.),
        d=length(p-q*h)-.6;
    e+=d;
}o+=vec4(-log(p*p),1)*3./i;
}
