#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))

void mainImage(out vec4 O, vec2 u){
    vec3 D, p, q, r = iResolution; 
    float f, i, s = 1., t = iTime; 
    u = (u - r.xy / 2.) / r.y;
    O *= 0.;

    D = normalize(vec3(u, 1));
    p.z -= .75;

    while(i++ < 300. && s > .1) {
        q = p;

        float j; 
        while(j++ < 5.)
            q.xy *= rot(t * .2),
            q.zx *= rot(t * .3),
            q = abs(q * 2.4) - .5;

        s = length(vec2(length(q.xz) - .32, q.y)) - .008;

        p += s * D / 600.;
    }

    O.rg += 1. / exp(i / 100.);
}
