////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
vec4 Nyan(vec2 Pos, float Rot, float Scl, vec2 FragCoord) {
    const vec2 Pxl  = vec2( 1.0/256.0,  1.0/32.0);
    const vec2 Siz  = vec2(40.0/256.0, 32.0/32.0);
    const vec2 SizH = vec2(20.0/256.0, 16.0/32.0);

    int Frame = int(floor(iTime*12.0)) % 6;   // 0 to 5

    vec2 UV    = rot(FragCoord-Pos, Rot) * Pxl;
         UV   /= Scl;                         //  Scale
         UV   += SizH;                        //  Position @ Center of sprite.
         UV    = clamp(UV, vec2(0.0), Siz);
         UV.x += float(Frame) * Siz.x;        //  Offset UV.x to current frame.
         UV.y += (Frame <= 1) ? -Pxl.y : 0.0; //  Fix frames 0,1 vertical position.

    return texture(iChannel3, UV);
}

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 FragColor, in vec2 FragCoord ) {
    vec4 Clr_BG = vec4(0.0, 0.2, 0.4, 1.0);

    vec2  MeowPos  = iResolution.xy*0.5;
          MeowPos += vec2( cos(iTime*1.1) * (iResolution.x*0.1875 ),
                           sin(iTime*1.9) * (iResolution.y*0.0625));

    float MeowRot  = 0.065 + sin(iTime*2.7)*0.1;

    float MeowScl  = (iResolution.y/96.0) + sin(iTime    )*0.2;

    vec4 Clr_Meow = Nyan(MeowPos, MeowRot, MeowScl, FragCoord);

    FragColor = mix(Clr_BG, Clr_Meow, Clr_Meow.a);
}
