void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 origUV = fragCoord/iResolution.x;
    vec2 uv=origUV-vec2(0.5);
    float scale = 40.+10.*sin(iTime)-10.*sin(uv.y);
    vec2 mdist = vec2(mod(scale*uv.x, 3.+sin(1.*iTime+uv.y*10.*fract(cos(uv.y)))),mod(7.*sin(uv.y+iTime),4.));
    mdist.y *= clamp(abs(sin(uv.x+iTime)),0.9,1.1);
    mdist.y += 2.*uv.y+10.*uv.x*abs(tan(uv.x+2.));
    float rain = distance(uv, uv+mdist-.5);
    float mask = step(distance(uv, uv+mdist-.5), .8);
    rain += mask;
    rain = 1.-rain;
    rain = clamp(rain, 0.0, 10.0);
    origUV += .2*rain;
    fragColor = mask*texture(iChannel0, 1.*origUV);
    fragColor += (1.-mask)*textureLod(iChannel0, 1.*origUV,2.5);

}
