//lanpanned from https://www.image-engineering.de/library/technotes/958-how-to-convert-between-srgb-and-ciexyz
vec3 srgbToLinear(vec3 col) {
  return mix(col/12.92,pow((col+.055)/1.055,vec3(2.4)),step(.04045,col));
}
vec3 linearToSrgb(vec3 col) {
  return mix(col*12.92,1.055*pow(col,vec3(1./2.4))-.055,step(.0031308,col));
}
vec3 linearToCiexyz(vec3 col) {
  return mat3(.4124564, .3575761, .1804375,
              .2126729, .7151522, .0721750,
              .0193339, .1191920, .9503041
              )*col;
}
vec3 ciexyzToLinear(vec3 col) {
  return inverse(mat3(.4124564, .3575761, .1804375,
                      .2126729, .7151522, .0721750,
                      .0193339, .1191920, .9503041
                      ))*col;
}
//note to self: lanpan from https://en.wikipedia.org/wiki/CIELUV
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(uv.x,fract(iTime/2.)*1.5,uv.y);
    col = linearToSrgb(ciexyzToLinear(col));
    if(any(greaterThan(col,vec3(1.)))||any(lessThan(col,vec3(0.)))) {
      fragColor = vec4(1.,0.,1.,1.);
      return;
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
