#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime
#define r iResolution

void mainImage(out vec4 o, vec2 u) {
    o -= o;
    u = (u - r.xy / 2.) / r.y;

    vec3 D = normalize(vec3(u, 1)),
         q, p, f;

    float i, j, e, d = 1.,
          fire, pipes, teto,
          nivel = cos(t) * .05 - .2;

    bool teste;
    while(i++ < 100. && d > .01) {

        e = 0., j = 1.;
        while(j < 512.)
            j += j,
            f = 2.*p * j * 8.,
            f.yz *= rot(j),
            e += abs(dot(sin(f*.1), vec3(1))) / j * .1;

        q = p;
        q.z += t;
        q = mod(q, 2.) - 1.;
        pipes = length(q.xz) - .2;
        fire = abs(q.z + nivel) - .02;
        teto = -abs(p.y) + 1.;

        d = min(
                teto,
                min(
                    pipes,
                    fire
                )
            ) + e * .5;

        p += D * d * .7;
    }

    o += vec4(1, 0, 0, 0) / exp(i * i * .003);
    o.g += 5. / i;

}
