void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    vec3 BGcol = vec3(0.3, 0.4, 0.5);    
    vec3 col = BGcol;
    float aspectRatio = iResolution.x/iResolution.y;
    uv.x *= aspectRatio;
    float time = iTime;
    float size1 = .15; 
    float size2 = .2;
    vec2 pos1 = vec2(sin(time) + 0.2, sin(time) +0.4);
    vec2 pos2 = vec2(cos(time)*0.8, cos(time)*0.2); 
    vec2 pos3 = vec2(0.5, 0.1);
    pos1 *= aspectRatio;
    pos2 *= aspectRatio;    
    pos3 *= aspectRatio;
    float d1 = length(uv - pos1) - size1;
    float d2 = length(uv - pos2) - size2;
    float d3 = uv.y-uv.x +0.5;
    float thk = 0.15;
    if (d1 < 0.0)
      col = vec3(0.5, 0.3, 1.0); 
    if (d2 < 0.0) 
      col = vec3(1.0, 1.0, 0.2);     
    if (d3 >= -thk && d3 <= thk)
      col = vec3(1.0, 0.055, 0.75); 
    fragColor = vec4(col,1.0);


    }
