#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,r=iResolution,q=vec3(-1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e-.1);
    p=vec3(log2(length(p)),exp(p.y)-.8,atan(p.x,p.z));
    p.xy*=R2D(iTime*.2);

    for(j=0.;j++<6.;)
        p=abs(abs(R3D(.3))-.12)-.08,       
        d=max(max(p.z,p.y),p.x),
        d-=length(p)/2e4;
    e+=d;
}o+=vec4(exp2(p),1)*3./i;
}
