void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord / iResolution.xy;

    vec3 strokeColor = vec3(0.0, 0.0, 0.0); // цвет полосок
    float lineThickness = 3.0 / iResolution.x; // толщина полосок в px
    float lineSpacing = 0.05; // расстояние между полосками

    float angle = -45.0; // угол наклона
    float radians = radians(angle);
    vec2 rotatedUV = vec2(
        uv.x * cos(radians) - uv.y * sin(radians), 
        uv.x * sin(radians) + uv.y * cos(radians)
    );

    float stripe = mod(rotatedUV.y, lineSpacing);

    float line = step(stripe, lineThickness);

    vec4 background = texture(iChannel0, uv);

    vec3 color = mix(background.rgb, strokeColor, line);

    fragColor = vec4(color, background.a);
}
