// From https://github.com/julesb/glsl-util

#define PI 3.14159265

#define cx_mul(a, b) vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x)
#define cx_div(a, b) vec2(((a.x*b.x + a.y*b.y)/(b.x*b.x + b.y*b.y)),((a.y*b.x - a.x*b.y)/(b.x*b.x + b.y*b.y)))
#define cx_sin(a) vec2(sin(a.x) * cosh(a.y), cos(a.x) * sinh(a.y))
#define cx_cos(a) vec2(cos(a.x) * cosh(a.y), -sin(a.x) * sinh(a.y))

vec2 as_polar(vec2 z) {
  return vec2(
    length(z),
    atan(z.y, z.x)
  );
}

vec2 cx_tan(vec2 a) {return cx_div(cx_sin(a), cx_cos(a)); }
vec2 cx_log(vec2 a) {
    vec2 polar = as_polar(a);
    float rpart = polar.x;
    float ipart = polar.y;
    if (ipart > PI) ipart=ipart-(2.0*PI);
    return vec2(log(rpart),ipart);
}
vec2 cx_pow(vec2 v, float p) {
  vec2 z = as_polar(v);
  return pow(z.x, p) * vec2(cos(z.y * p), sin(z.y * p));
}

// From https://iquilezles.org/www/articles/palettes/palettes.htm

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos(2. * PI *(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = vec2(2)*fragCoord/iResolution.xy - vec2(1);
    vec2 z = uv;

    // pick two points in range
    vec2 p = vec2(-0.25, 0.5);
    vec2 q = vec2(0.5, -0.25);

    // compute the rational function
    vec2 rat_pol = cx_div((z - p), (z - q));

    // Calculate the imaginary part of log of our rational polynomial
    float imaginary = cx_log(rat_pol).y / PI;

    // palette parameters
    float t = iTime + 50.;
    vec3 pal_1 = vec3(0.6, 0.4, 0.4);
    vec3 pal_2 = vec3(0.2, 0.1, 0.1);
    vec3 pal_3 = vec3(0.3, t*0.1, t*0.1);
    vec3 pal_4 = vec3(t*0.2, -0.5*t, -0.5*t);
    vec3 col = palette( imaginary, pal_1, pal_2, pal_3, pal_4);


    // Output to screen
    fragColor = vec4(col,1.0);
}
