void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float t = iTime;
    float cosT = cos(t);
    float sinT = sin(t);

    float x = uv.x;
    float y = uv.y;
    float d = length(uv);

    float r = 0.0;
    float g = 0.0;
    float b = 0.0;

    x = x + sin(x) * 0.01;
    x = min(x, 1.0 - x);

    y = y + cos(y) * 0.01;
    y = min(y, y - 1.0);

    d = d * 10.0 * sin(t / 20.0);

    r = abs(cos(length(vec2(dot(d * (1.0 - cosT), d + (0.5 - x)), dot(y, 1.0 - sinT * 2.0 + d))) * 10.0 * sin(t - cos(t))));

    d = d + 0.01;

    g = abs(cos(length(vec2(dot(d * (1.0 - cosT ), d + (0.5 - y)), dot(y, 1.0 - sinT * 2.0 + d))) * 10.0 * sin(t - cos(t))));

    d = d + 0.01;

    b = abs(cos(length(vec2(dot(d * (1.0 - cosT ), d + (0.5 - x)), dot(y, 1.0 - sinT * 2.0 + d))) * 10.0 * sin(t - cos(t))));

    // Output to screen
    fragColor = vec4(r, g, b, (r + g + b) / 3.0);
}
