#define COLOR1 vec4(.99, .85, .43, 1.)
#define COLOR2 vec4(.99, .18, .38, 1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /// uv
    vec2 uv = fragCoord / iResolution.xy;
    vec4 col = vec4(0.0);
    float t = fract((sin(iTime * 0.5) + 1.0) / 2.0);

    /// functions to uncomment or alter
    // float f1 = uv.x;
    // float f2 = smoothstep(0.0, 1.0, uv.x);

    float f1 = uv.x * (1.0 - uv.x) * 4.0;
    float f2 = pow(uv.x, 5.0);

    /// line
    float val = mix(f1, f2, t);
    float line = smoothstep(0.001, 0.0075, abs(uv.y - val));
    line = 1.0 - line;

    /// render
    col = mix(mix(COLOR1, COLOR2, val), vec4(line) * uv.x, line);
    fragColor = col;
}
