// 182 chars by SnoopethDuckDuck :D
void mainImage(out vec4 o, vec2 u)
{
    o *= 0.; // (I think o -= o breaks on some machines)
    vec3 r = iResolution, p;

    for (float t, n; 
         ++t < 8.; 
         t -= 
         o.b = max(o.b, 
         n = abs(atan(dot(sin(
         p = vec3((u-.5*r.xy) / r.y * t, t + iTime - 5.)),cos(p.yzx))))))

        o.gr += n / 2e2;	
}

