void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 mouse = iMouse.xy / iResolution.xy;

    // uv = uv - vec2(cos(iTime),tan(iTime));
    uv = uv - vec2(mouse + 0.5);
    uv = uv + vec2(sin(uv.y * 10.0 + iTime), cos(uv.x * 10.0 + iTime)) * 0.1;


    vec3 tex = texture(iChannel0, uv).rgb;

    float avg = (tex.r + tex.g + tex.b) / 3.0;

    if(avg > 0.2 + 0.5 * abs(sin(iTime * 0.05)))
        tex = vec3(1.0);
    else
        tex = vec3(cos(iTime),sin(iTime),tan(iTime));

    fragColor = vec4(tex,1.0);
}
