void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy / iResolution.xy;

    vec3 col = sqrt(texture(iChannel0, coord).rgb);

    col = mix(vec3(0.0, 0.0, 1.0), vec3(0.0, 1.0, 0.0), col * 5.0) * col;

    fragColor = vec4(pow(col, vec3(0.4545)),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy / iResolution.xy;
    float ar = iResolution.y / iResolution.x;

    vec2 gridRes = vec2(12.0, 8.0);
    float gridAr = gridRes.y / gridRes.x;
    vec2 grid = fract(coord * gridRes);
    vec2 gridID = floor(coord * gridRes);
    vec2 gridCoord = (grid - 0.5) / min(vec2(ar / gridAr, gridAr / ar), 1.0);
    gridCoord = gridCoord * 2.5;

    vec3 col = texture(iChannel0, coord).rgb;

    if (iMouse.z > 0.5) col = vec3(0.0);

    gridCoord += vec2(cos(iTime * (gridID.x + 1.0)), sin(iTime * (gridID.y + 1.0)));

    col = min(vec3(mix(col.r, step(length(gridCoord), 0.05), 0.002)), 1.0);

    fragColor = vec4(col,1.0);
}
