void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.0);

    col = texture(iChannel0, uv.xy).rgb * (1.0 - texture(iChannel1, uv.xy).rgb);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - 0.5) * 2.0 + 0.5;

    // Time varying pixel color
    vec3 col = vec3(1.0);

    col = texture(iChannel0, uv.xy).rgb;
    col *= max(0.0, 1.0 - step(0.45, abs(uv.x - 0.5)) - step(0.45, abs(uv.y - 0.5))); 

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.0);

    col = texture(iChannel0, uv.xy).rgb;

    col = vec3(step(0.5, fract(length(col) - beat / 4.0))) * step(col.g, col.r + col.b);
    col *= max(0.0, 1.0 - step(0.45, 2.0 * abs(uv.x - 0.5)) - step(0.45, 2.0 * abs(uv.y - 0.5))); 

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv_fb = uv;
    //uv_fb -= vec2(0.0, 16.0) / iResolution.xy;
    uv_fb = (uv_fb -0.5) / 1.01 + 0.5;


    // Time varying pixel color
    vec3 col = vec3(1.0);

    col = fract( vec3(1.2, 1.5, 1.7) * texture(iChannel0, uv.xy).rgb + beat);
    col = mix(col, fract(texture(iChannel1, uv_fb.xy).rgb - 1.0 / 255.0), 1.0 - step(0.5, length(texture(iChannel2, uv.xy).rgb)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
