void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec3 colors[20] = vec3[20](
        vec3(0.7200, 0.7800, 0.4337), // yellow
        vec3(0.6035, 0.4022, 0.3490), // light red (pink, closest match)
        vec3(0.4074, 0.2165, 0.1688), // red
        vec3(0.2625, 0.2243, 0.0),    // brown
        vec3(0.2060, 0.1580, 0.4753), // blue
        vec3(0.4361, 0.2388, 0.5255), // purple
        vec3(0.4235, 0.4235, 0.4235), // grey
        vec3(0.3455, 0.5493, 0.2629), // green
        vec3(0.4370, 0.6432, 0.6980), // cyan
        vec3(0.6047, 0.8238, 0.5176), // light green
        vec3(0.4370, 0.6432, 0.6980), // cyan
        vec3(0.3455, 0.5493, 0.2629), // green
        vec3(0.4235, 0.4235, 0.4235), // grey
        vec3(0.4361, 0.2388, 0.5255), // purple
        vec3(0.2060, 0.1580, 0.4753), // blue
        vec3(0.2625, 0.2243, 0.0),    // brown
        vec3(0.4074, 0.2165, 0.1688), // red
        vec3(0.6035, 0.4022, 0.3490), // light red (pink, closest match)
        vec3(0.7200, 0.7800, 0.4337),  // yellow
        vec3(1., 1., 1.)  // white
    );


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;

    //uv.x += iTime;

    int idx = int(mod(uv.x, 1.) * 20.);

    idx += int(iTime * 32.);
    idx = int(mod(float(idx), 20.));

    // flat
    col = colors[idx];
    // smooth
    //col = mix(colors[idx], colors[int(mod(float(idx) + 1., 20.))], fract(uv.x * 20.));


    // Output to screen
    fragColor = vec4(col,1.0);
}
