    // basic remap func
float map(float value, float start1, float stop1, float start2, float stop2)
{
    return start2 + (stop2 - start2) * ((value - start1) / (stop1 - start1));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        // you know the setup by now...
    float angle = iMouse.x / iResolution.x;
    vec2 uv = fragCoord / iResolution.xy;

        // coord setup
    float dx = (uv.x - 0.5) * 2.;
    float dy = (uv.y - 0.5) * 2.;

        // make sphere
    float scaleY = sqrt(1. - dy * dy);
    float angY = 0.5 + asin(dy) / 3.1416;
    float angX = (0.5 + asin(dx / scaleY) / 3.1416) / 2. + angle;

    float maskAngX = angX + (iTime / 10.);

    float nightinterpolator = 1. - texture(iChannel2, vec2(maskAngX + (iTime / 10.), angY)).a;

        // mash it together
    fragColor = mix(
        texture(iChannel0, vec2(angX, angY)), 
        texture(iChannel1, vec2(angX, angY)), 
        nightinterpolator);

    fragColor += texture(iChannel3, vec2(angX + (iTime / -269.), angY))
        * min(nightinterpolator + 0.2, 1.);
    fragColor *= mix(1., 0., map(clamp(dx * dx + dy * dy, 0.92, 1.), 0.92, 1., 0., 1.));
}

    // channel 0 is the night texture of earth from https://www.solarsystemscope.com/textures/
    // channel 1 is the day texure of earth
    // channel 2 is a custom night mask i made in paint.net
    // channel 3 is the cloud texture of earth
