float num_iter, objid, glow=0., glow2=0., mmaxDist=50.;
vec3 ifs_color, ro, light_dir;

mat2 rot(float an) {return mat2(cos(an),-sin(an),sin(an),cos(an));}

float random(vec2 p) {return fract( sin( dot( p, vec2(12., 90.)))* 1e4 );}


float de(vec3 p) {
  vec3 origP= p;  
  ifs_color = vec3(0.);  

  float nn = 1.8;
  p.xz = mod(p.xz  + nn, 2.*nn) -nn; p.yz = mod(p.yz  + nn, 2.*nn) -nn;  
  float shellNum =  p.z/20.;  
  vec4 q0 = vec4 (p, 2.), q = q0; float i=0.;
  for (; i<params[1]; i ++) {
    q.xyz = clamp (q.xyz, -1.-params[5], 1.+params[5]) * (2.+params[4]) 
            - (1.+params[3])*q.xyz; 

    q = q * (2.52+params[2] - shellNum  ) 
        / clamp (  max( dot(q.yz,q.yz), max(dot (q.xz, q.xz),  dot(q.xy,q.xy) ) ), 
        (-1.+params[4])-params[0], 1.+params[0]) + q0 ;

    if (q.x*q.y > 0. ) ifs_color.x ++;
    if (q.y*q.z > 0. ) ifs_color.y ++;
    if (q.z*q.x > 0. ) ifs_color.z ++;  
  } 
  ifs_color /= i;
  objid = 0.;
  return max( length (q.xyz) / q.w, -(length(origP-ro)-params[7] ) ) ; 
}

float march(vec3 ro, vec3 rd, float mx) {

    float t=0.,eps = 1e-5, distfac=10., hitThreshold = eps;
    for(int i = 0; i < 200; i++) {
        vec3 pos = ro + rd*t;
        float d = de(pos);      
        if (objid == 1. || objid == 3.) { return d; }       
        if (d < hitThreshold || t >= mx) break;
        t += d;
        hitThreshold = eps*(1.+t*t*distfac)*(1.+random(pos.xz)*30.);       
        float zz = pos.y - light_dir.y;  zz*=zz;
        glow += exp(-max( 8.*(1.-exp(-zz*4.))-d,0.)/10.);        
        float zz2 = ro.z - 0. - pos.z; zz2*=zz2;        
        glow2 += exp( -max(1.-d,0.)/300.);        
    }
    return t;
}

vec3 render(vec3 ro, vec3 rd) {
    float tt = mod(iTime/3.,5.), tf = tt*tt; 
    vec3 col = vec3(0.);
    light_dir = ro ;  light_dir.y += tf;
    vec3 color = vec3( params[27], params[28], params[29]);
    float t = march(ro, rd, mmaxDist);
    vec3 glowCol;
    vec3 pos = ro + rd*t; 
    vec3 glowSource = light_dir;
    float glowStr = exp( -length(pos.y - glowSource.y) / 5.);
    glow *= glowStr; 
    float glowStr2 = (exp( -length( pos - ro + vec3(0,0,0.) ) / 6.));
    glow2 *= glowStr2;          
    vec3 addColor = vec3( cos(ifs_color.x*15.), cos(ifs_color.y*4.), cos(ifs_color.z*5.) );      
    color = addColor;
    color*=color;  
    glowCol = .5*(color*color);

    vec3 band = vec3(.2*cos(iTime),.2*sin(iTime/2.),.3*sin(iTime*2.));
    band *= band;
    return   6e-4*pow(glow2,10.)*glowCol + 1e-7*pow(glow,14.)*band*glowCol
        + .01*(cos(pos.x/15.+iTime*1.7))*1e-9*glowCol;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p  = (2.*fragCoord.xy-iResolution.xy)/iResolution.y*8.;    
    ro = vec3( 0 ) ;     
    vec3 rd = normalize( vec3(p,1.2 * (1.+.2*sin(iTime/2.) ))) ;


    rd.yz *= rot(-1.2);
    ro.y += iTime*6.;
    rd.xy *= rot(.4);

    vec3 col = clamp(render(ro, rd),1e-4,1e6);
    col = pow ( ( 1. - exp(-.4 * (1.+params[16]) * col) ) , vec3(.3) );

    fragColor = vec4( col, 1.);

}
