#define PI 3.14159265359
#define PI_2 6.2831


vec3 colorPalatte(float d, float angle)
{
	float theta = mod(3.0 + 3.0 * angle /PI +1.5 , 6.0);
	vec3 result = vec3(0.0);
	result = clamp(abs(mod(theta + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
	result = mix(vec3(1.0), result, d);
	return result;	
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =   fragCoord / iResolution.xy;
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float r = 1.0;

    float dis = (length(p)-r);
    float angle = atan(p.x, p.y);

    vec3 col = (dis>0.0) ? vec3(0.0) : colorPalatte(length(p), angle);
    col = mix( col, vec3(0.0), 1.0-smoothstep(0.0,0.01,abs(dis)));


    // Output to screen
    fragColor = vec4(col, 1.0);
}
