#define pi 3.14159
#define dir(a) vec2(cos(a),sin(a))

void mainImage( out vec4 o, vec2 u )
{
    vec2 R = iResolution.xy;
    u = (u+u-R)/R.y;      
    o *= 0.;

    for (float i, n = 9.; i++ < n;) {
        vec4 c = cos(vec4(0,pi,0,0)
                   + 2.*length(3.*u - 3.*dir(2.*pi*i/n)));

        o += cos(36.*c - 7.*iTime) / cosh(9.*c);        
    }

    o = sqrt(tanh(.6 * o));
}
