void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = 15. + iTime * 15.;
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float threshold = 7.0; 
    float loopedTime = mod(iTime, 2.0 * threshold); 
    if (loopedTime > threshold) 
    { 

        loopedTime =2.0 * threshold - loopedTime;
    }

    t = loopedTime;
    float radius = -2.;
    float dist = sin(length(uv)*t * radius);
    float circle = smoothstep(radius, radius + 0.01, dist) - smoothstep(radius - 0.01, radius, dist); 


    // Time varying pixel color
    uv+=dist;
    vec3 col = 0.5 + 1.25*cos(t*uv.xyx*vec3(4,2,0));
    // Output to screen
    fragColor = vec4(col.x,col.y,col.z,.1);

}
