vec3 palette(float t)
{
    vec3 a = vec3(0.838, 0.428, 0.698);
    vec3 b = vec3(0.078, 0.230, 0.294);
    vec3 c = vec3(-1.562, 1.728, 2.000);
    vec3 d = vec3(-1.032, 0.308, -0.252);

    return (a + b*cos( 6.28318*(c*t+d)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    // centred pixel coordinates
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    //fix aspect ratio
    uv.x *= iResolution.x/iResolution.y;
    //on garde le vecteur centre pcq apres on le fract
    vec2 uv0 = uv;
    //on set un vec finalColor pour pouvoir itérer
    vec3 finalColor = vec3(0.0);

for (float i = 0.0; i < 4.0; i++)
{
    uv *= 1.5;
    uv = fract(uv);
    uv -= 0.5;
//calcule la distrance du vecteur a lorigine avec length.
   float d = length(uv) * exp(-length(uv0));

   vec3 col = palette(length(uv0) + i*0.4 + iTime*0.3);
//On definit des cerlces
//sin de d*8. car entre 0 et 1 les diff sont nulles
//La on produit donc 8 cycles complet de sinus
//iTime var globale secondes
// /8 le tout pour empêcher les ondulations detre trop fortes
    d = sin(d*8. + iTime)/8.;
//Avant dutiliser abs limage a ungradient qui differencie clairement l'intérieur du cercle (neg) de l'exterieur (pos)
//On rend les valeurs absolues 
   d = abs(d);

//Premier para de step pour definir un seuil
// si c a linterieur -> 0
// a lext -> 1 
//smoothstep pour faire transi a linterieur des paras 
//  d = smoothstep(0.0, 0.1, d);
    // Output to screen

   d = pow(0.01 / d, 1.2);

   finalColor += col *= d;
}

    fragColor = vec4(finalColor, 1.0);
}
