renderpass.code:
#define s(a,b,c) smoothstep(a,b,c)
#define experimental

float density = 6.0;
float dotsize = 5.0;


highp float rand(vec2 co) {
    return fract(cos(mod(dot(co.xy+0.12,vec2(12.9898,78.233)),3.14))*43758.5453);
}
highp vec3 rand3(vec2 co){
	float t=rand(co);
	float z=rand(co+t);
	return vec3(t,z,rand(co+z));
}
vec3 getpoint(vec2 id){
	vec3 r=rand3(id);
	vec3 ou=vec3(
		sin(iTime*r.x),
		cos(iTime*r.y),
		sin(iTime*r.z*10.)
	);
	return ou*0.45+0.5;
}
vec3 getpointcolor(vec2 id){
	vec3 r=rand3(id);
	vec3 ou=vec3(
		sin(iTime*r.x),
		cos(iTime*r.y),
		sin(iTime*r.z*10.)
	);
	return ou*0.5+0.5;
}

float line(vec2 uv, vec2 p, vec2 p2){
	vec2 n=uv-p;
	vec2 w=p2-p;
	float r=clamp(dot(n,w)/dot(w,w),0.,1.);
	r=length(n-w*r);
    #ifdef experimental
	return clamp(0.01/r,0.,1.)*(s(0.6,1.,1./distance(p,p2)));
    #else
    return clamp(s(0.04,0.01,r)*(s(0.6,1.,1./distance(p,p2))),0.,1.);
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	uv*=density;//density

	vec2 id=floor(uv);
    vec2 c=fract(uv);

    vec2 p[9];
    vec3 colors[9];
	vec3 col= vec3(0.);
    int i=0;
    for(float n=-1.;n<=1.;n++){
        for(float w=-1.;w<=1.;w++){
			vec2 nc=id+vec2(w,n);
			vec3 point=getpoint(nc);
			p[i]=point.xy+vec2(w,n);
            colors[i]=getpointcolor(nc);
			vec2 a=(p[i].xy-c)*dotsize;//dot size
            //colors[i] = vec3(0.5*(1.+sin(iTime*0.5*float(i))),0.5*(1.+cos(iTime*1.5*float(i))),0.5*(1.+sin(iTime*3.5*float(i))));
			col+=0.5*colors[i]*min(point.z/dot(a,a),5.0);
            i++;
		}
	}

    for(int i=0;i<9;i++){
		col+=pow(line(c,p[i].xy,p[4].xy),2.2);
	}
	col+=pow(line(c,p[1].xy,p[3].xy),2.2);
	col+=pow(line(c,p[1].xy,p[5].xy),2.2);
	col+=pow(line(c,p[7].xy,p[3].xy),2.2);
    col+=pow(line(c,p[7].xy,p[5].xy),2.2);

    col.x=pow(col.x,1./2.2);
    col.y=pow(col.y,1./2.2);
    col.z=pow(col.z,1./2.2);
    col*=col;
    col*=0.5;

    fragColor = vec4(vec3(col),1.0);
}
