/**  // 287 chars

#define S smoothstep

void mainImage( out vec4 O, vec2 u )
{
     vec2  R = iResolution.xy,
           U = 5.*( u+u - R ) / R.y * mat2(cos(iTime-vec4(0,11,33,0))); // rotating frame
     float a = atan(U.y,U.x), l = length(U),                            // polar coordinates
           L = 6.28*l+a; //, d = sin(L);                                // for isolines
     U *= sign(a);                                                      // symmetry for lines
     O = vec4( L < 6.28*4.3 + ( a > 0. ? 3.14 :0. )                     // --- spiral part
                  ? // S(1.5,0.,abs(d)/fwidth(d))                       // AA spiral (fwidth cause a pixel gap between spiral and line)
                       S(1.5,0.,abs(tan(L))/62.8*R.y)                   // AA spiral (analytical derivative )
                    // ( S(1.5,0.,abs(tan(L))/62.8*R.y) + S(.1,0.,length( vec2(fract(l +a/6.28*36.)-.5,fract(L/3.14)-.5) )) ) // dressed with dots
                     * S(1.,3.,l)                                       // central deeming
                  :    S(15./R.y,0.,abs(U.x+4.5))                       // --- lines part
             );
}

/**/


// 247 chars: -35 chars by iapafoto, -9 by SnoopethDuckDuck


void mainImage( out vec4 o, vec2 u )
{
    o.xyz = iResolution;    
    float l = 31.4*length( u = (u+u-o.xy)/o.y 
                             * mat2(cos(iTime-vec4(0,11,33,0)))),
          a = atan(u.y, u.x) + l;

    o -= o -   smoothstep(6.,19.,l)
             * smoothstep(3./o.y,0., abs( a < ( a>l ?30.14:27.) 
                                            ? tan(a)/31.4 
                                            : u.x*sign(a-l) + .9 ));                                          
}
