void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Center the coordinates
    uv -= 0.5;

    // Scale and rotate coordinates
    uv *= 2.0;
    uv = mat2(tan(0.5), -tan(12.5), sin(0.5), cos(0.5)) * uv;

    // Create a basic spiral galaxy
    float radius = length(uv);
    float angle = atan(uv.y, uv.x);
    float spiralDensity = 0.1;
    float brightness = smoothstep(1.1, .01, radius) * exp(-spiralDensity * radius);

    // Add some random noise
    float noise = fract(sin(dot(uv, vec2(112.9898, 718.233))) * 43758.5453);
    brightness += noise * 0.2;

    // Output final color
    fragColor = vec4(vec3(brightness), 1.0);
}
