// Fork of "infinite star zoom (160 chars)" by FabriceNeyret2. https://shadertoy.com/view/MtdfWH
// 2023-12-27 08:00:15
// Golfed to 323 chars by removing the smoothstep:
#define A(a) texture(a, (U+U-iResolution.xy)*s/4e4 + i/L)
void mainImage(out vec4 O, vec2 U)
{
    vec4 c[4];
    float k, s, i, L = 20., T = 4.*fract(iTime / 40.);
    for( O *= s; i++ < L;
       O += ( 1. - cos(6.3*k/L) ) / s *
       (8.* mix( c[int(T)], c[(int(T) + 1) % 4], fract(T)) - 1.2))
     s = exp2( k = mod( i-iTime, L ) ),
     c[0] = A(iChannel0),
     c[1] = A(iChannel1),
     c[2] = A(iChannel2),
     c[3] = A(iChannel3);
}

