void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Slow-moving, large-scale noise for displacement
    float noiseScale = 0.08; // Adjust for larger/smaller noise pattern
    float noiseSpeed = 0.005; // Adjust for faster/slower movement
    vec2 noiseUV = uv * noiseScale + vec2(iTime * noiseSpeed, 0.5);
    vec4 noise = texture(iChannel2, noiseUV);

    // Apply displacement to UV coordinates
    float displacementStrength = 0.01; // Adjust for stronger/weaker displacement
    uv += (noise.rg - 0.5) * displacementStrength;

    // Fetch the texture from iChannel1
    vec4 textureColor = texture(iChannel1, uv);

    // Fetch the previous frame from Buffer A (iChannel0)
    vec4 previousFrame = texture(iChannel0, uv);

    // Blend control
    float blendAmount = 0.98; // Adjust as needed

    // Mix the texture color with the previous frame based on the blendAmount
    fragColor = mix(textureColor, previousFrame, blendAmount);
}
