float rand(vec2 n) {
	return fract(cos(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float fbm(vec2 n) {
	float total = 0.0, amplitude = 1.0;
	for (int i = 0; i < 3; i++) {
		total += noise(n) * amplitude;
		n += n;
		amplitude *= 0.5;
	}
	return total;
}

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    const vec3 c1 = vec3(0.2);
	const vec3 c2 = vec3(0.3);
	const vec3 c3 = vec3(0.5);
	const vec3 c4 = vec3(0.7);
	const vec3 c5 = vec3(0.4);
	const vec3 c6 = vec3(0.7);

	vec2 speed = vec2(0.1, 0.5);
	vec2 p = fragCoord.xy * 5.0 / iResolution.xx;
	float q = fbm(p - iTime * 0.1);
	vec2 r = vec2(fbm(p + q + iTime * speed.x - p.x - p.y), fbm(p + q - iTime * speed.y));
	vec3 c = mix(c1, c2, fbm(p + r)) + mix(c3, c4, r.x) - mix(c5, c6, r.y);

    c = c * vec3(1.0 - smoothstep(0.1, 0.8, uv.y));

    float sinX = 1.0 - sin(uv.x * 3.14) / 0.8;
    float g = smoothstep(sinX, sinX + 0.6, uv.y);


    fragColor = vec4(c * (1.0 - vec3(g)),0.);
}
