void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Time varying colorful pattern
    vec3 patternColor = 0.5 + 0.5 * cos(iTime + vec3(0, 2, 4));

    // Calculate distance to the center
    vec2 center = vec2(0.5, 0.5);
    float distanceToCenter = length(uv - center);

    // Create concentric circles with changing colors
    float circles = sin(distanceToCenter * 20.0 + iTime) * 0.5 + 0.5;

    vec3 col = mix(patternColor, vec3(1.0 - circles, circles, 1.0), 0.7);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
