#define R3D(a) ( mix( q*dot(p,q), p, cos(a) ) + sin(a)*cross(p,q) )
#define R2D(a)       mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, vec2 FC )
{
float i,j,d=.1,e; vec3 p,R=iResolution, q=vec3(-1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*R.xy)/R.y,e);
    p.z-=4.;
    p.xz*=R2D(iTime *.1);

    for(j=0.;j++<4.;)
        p=abs(abs(mod(p*R3D(5.77),.3)))-.005,
        d=max(dot(p,p),cos(p.y))*atan(p.x,p.z);
    e+=d;
}o+=vec4(sqrt(p),1)*4./i;
}
