const float PI = 3.14;
// Take this and multiply your UV by the resulting mat2 to get the rotation
mat2 rotationMatrix(float angle)
{
	angle *= PI / 180.0;
    float sine = sin(angle), cosine = cos(angle);
    return mat2( cosine, -sine,
                 sine,    cosine );
}
void mainImage(out vec4 fragColor, vec2 fragCoord){
    vec4 color = vec4(1.0,1.0,1.0,1.0);
    float contrast = 0.1; //exposure
    float brightness = 0.5; //set according to contrast
    float zoom = 0.6;
    float messiness = 1.2; //1.0 curtainy, 5.0 silky
    float fall = 0.3;
    float side = 0.5;
    float chunks = 20.0; // 20, but adjust according to scale
    float wave = 0.02; // 0.02
    float waveRepeat = 8.0; // 8.0

    vec2 uvOrg = fragCoord/iResolution.y;
    vec2 uv = uvOrg/zoom;
    uv *= rotationMatrix(45.0);

    float t = 0.3*iTime;
    float tsin = 0.5 * sin(iTime/2.0);

	uv.y += wave*sin(waveRepeat*uv.x-t);
    float f = brightness + contrast*sin(messiness*(uv.x+uv.y+cos(3.0*uv.x+5.0*uv.y)+fall*t)+sin(chunks*(uv.x+uv.y-side*tsin)));

    vec4 dsp = color*vec4(f);
    float displacement = dsp.x - 0.5;

    vec4 col = texture(iChannel0, uvOrg.xy + vec2(-1, 0) * f * 0.1);
    fragColor = col;

}
